mapper.select:

select

from tbl_shelf_position_information
where id = #{id,jdbcType=VARCHAR}
and shelf_position_id = #{shelfPositionId,jdbcType=VARCHAR}
and shelf_position_code = #{shelfPositionCode,jdbcType=VARCHAR}

mapper.delete:

delete from tbl_shelf_position_information
where id = #{id,jdbcType=VARCHAR}
and shelf_position_id = #{shelfPositionId,jdbcType=VARCHAR}
and shelf_position_code = #{shelfPositionCode,jdbcType=VARCHAR}

mapper.select:
SELECT * FROM tbl_shelf_position_information


        AND tbl_shelf_position_information.warehouse_type =#{warehouseType}


        AND tbl_shelf_position_information.is_it_empty=#{isItEmpty}


        AND tbl_shelf_position_information.commodity_id=#{CommodityId}


ORDER BY
tbl_shelf_position_information.shelf_position_code ASC
LIMIT 0,#{count}

mapper.update:
update tbl_shelf_position_information




                when shelf_position_id=#{item.shelfPositionId} then #{item.isItEmpty}






                when shelf_position_id=#{item.shelfPositionId} then #{item.commodityDetaidListBeanWarehousingId}






                when shelf_position_id=#{item.shelfPositionId} then #{item.detailedListWarehousingId}






